package Day1;

public class Char_Java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='a'; //assigning single character
		//This line declares a variable named "ch" of type char and assigns the value 'a' to it.
		System.out.println(ch);
		
		char ch1=65;
		System.out.println(ch1);
		//This line declares a variable named "ch1" of type char and assigns the 
		//integer value 65 to it. The integer value 65 represents the ASCII code for the 
		//character 'A'. This line then prints the value of the variable "ch1" to the console.
		
		char var1= '\u00A7';
		System.out.println(var1);
		
		char var2= '\u20AC';
		System.out.println(var2);
		
		int a='A';
		System.out.println(a);
	}

}
