package Day1; //package declaration 

public class data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value1=9/2;
		
		
		float value2=101/61;
		//value2 - identifier
		/*Identifiers are names given to various program elements such as classes, methods,
		 *  variables, etc. They help to identify and differentiate these elements from 
		 *  each other. In Java, identifiers 
		 * are case sensitive, which means that "value1" and "Value1" are two different identifiers.*/
		 
		double value3=10d/6d;
		float value4=5/2.5f;
		
		
		System.out.println("value1=" + value1);
		System.out.println("value2=" + value2);
		System.out.println("value3=" + value3);
		System.out.println("value4=" + value4);
		

	}

}
